import React from "react";
import { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {
  const [selectedFilter, setFilterValue] = useState("");

  const filterSelectHandler = (event) => {
    setFilterValue(event.target.value);
  };

  const selectHndler = (event) => {
    event.preventDefault();

    const filterData = {
      filter: selectedFilter,
    };

    props.onSelectFilter(filterData);

    setFilterValue("");
  };

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onSelect={selectHndler}>
          <option value="2022" onSelectOption={filterSelectHandler}>
            2022
          </option>
          <option value="2021" onSelectOption={filterSelectHandler}>
            2021
          </option>
          <option value="2020" onSelectOption={filterSelectHandler}>
            2020
          </option>
          <option value="2019" onSelectOption={filterSelectHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
