import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import "./ExpensesFilter";
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");

  const saveSelectedFilter = (filterData) => {
    setFilteredYear(filterData);
  };

  const filteredExpenses = props.items.filter((expense) => {
    return expense.date.getFullyYear().toString() === filteredYear;
  });

  return (
    <Card className="expenses">
      <ExpensesFilter
        default={filteredYear}
        onChangeFilter={saveSelectedFilter}
      />
      {filteredExpenses.items.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
};

export default Expenses;
